import React, { useEffect, useMemo } from "react";
import axios from "axios";
import "../../style/styles.css";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { cargarTablaLiquidaciones } from "../../actions";
import { NavLink } from "react-router-dom";
import printer from "../../utils/printer.png";
import mail from "../../utils/mail.png";
import Table from "react-bootstrap/Table";

export default function TablaLiquidacion({
  ciclo,
  idCuota,
  nombreCuota,
  idAlumno,
}) {
  const dispatch = useDispatch();
  const liquidaciones = useSelector((state) => state.liquidaciones);

  function cargarTabla() {
    axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/traerliquidacionesalumnos.php?ciclo=" +
          ciclo +
          "&idcuota=" +
          (idCuota ? idCuota : 0) +
          "&idalumno=" +
          idAlumno
      )
      .then((response) => {
        if (response.data) {
          return dispatch(cargarTablaLiquidaciones(response.data));
        } else {
          return dispatch(cargarTablaLiquidaciones([]));
        }
      });
  }

  useEffect(() => {
    cargarTabla();
  }, [ciclo, idCuota, idAlumno]);

  function handleClick(e, l) {
    e.preventDefault();
    document.getElementById(`spinner ${l.idalumno}`).classList.remove("d-none");
    if (l.traspaso === "1") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/revertircuota.php?ciclo=" +
            ciclo +
            "&idcuota=" +
            idCuota +
            "&idcurso=" +
            l.idcurso +
            "&idnivel=" +
            l.idnivel +
            "&idalumno=" +
            l.idalumno
        )
        .then((response) => {
          if (response.data !== "") {
            Swal.fire({
              title: "Error al Revertir Liquidación!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            });
          }
        });
    } else {
      axios.post(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/liquidarcuota.php?" +
          "idcuota=" +
          idCuota +
          "&idcurso=" +
          l.idcurso +
          "&userid=1" +
          "&idalumno=" +
          l.idalumno
      );
      cargarTabla();
    }
    setTimeout(() => {
      document.getElementById(`spinner ${l.idalumno}`).classList.add("d-none");
      cargarTabla();
    }, 800);
  }

  function imprimirLiquidaciones(e, l) {
    e.preventDefault();
    window.open(
      "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/imprimir_boletapago.php?" +
        "idcuota=" +
        idCuota +
        "&idalum=" +
        l.idalumno
    );
  }

  function enviarLiquidaciones(e, l) {
    e.preventDefault();
    axios
      .post(
        "https://www.califcolegios.wnpower.host/donboscocastelar/aranceles/liquidacion/enviar_boletapagoweb.php?idcuota=" +
          idCuota +
          "&idalum=" +
          l.idalumno +
          "&cuota=" +
          nombreCuota
      )
      .then((response) => {
        console.log(response?.data);

        if (response?.data?.toString() === "") {
          Swal.fire({
            title: "El Correo ha sido enviado con EXITO",
            icon: "success",
            html: `${response.data}`,
            showCloseButton: true,
          });
        } else {
          Swal.fire({
            title: "Error al Enviar el Correo!",
            icon: "error",
            html: `${response.data}`,
            showCloseButton: true,
          });
        }
      });
  }

  return (
    <div>
      <Table
        responsive
        striped
        hover
        bordered
        className="w-100 table-condensed text-center"
      >
        <thead className="py-5 bg-light sticky-top">
          <tr>
            <th className="col-3">Alumno</th>
            <th className="col-1">Nivel</th>
            <th className="col-1">Curso</th>
            <th className="col-1">Fecha Liquidación</th>
            <th className="col-1">Acción</th>
            <th className="col-1">Imprimir</th>
            <th className="col-1">Enviar Correo</th>
          </tr>
        </thead>
        <tbody>
          {liquidaciones &&
            liquidaciones.map((l) => (
              <tr key={l[2]}>
                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap text-left"
                  >
                    {l.alumno}
                  </p>
                </td>
                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap "
                  >
                    {l.nivel}
                  </p>
                </td>
                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap "
                  >
                    {l.nombre}
                  </p>
                </td>

                <td>
                  <p
                    style={{ fontSize: "13px" }}
                    className="fw-bold text-nowrap mx-4"
                  >
                    {l.fhliq}
                  </p>
                </td>

                <td className="px-5">
                  <div>
                    <button
                      className={
                        l.traspaso === "0"
                          ? "btn rounded btn-success btn-sm"
                          : "btn rounded btn-danger btn-sm"
                      }
                      onClick={(e) => {
                        handleClick(e, l);
                      }}
                    >
                      {l.traspaso === "0" ? "Liquidar" : "Revertir"}
                    </button>
                    <div
                      id={`spinner ${l.idalumno}`}
                      className="ml-3 mt-2 spinner-border spinner-border-sm spinner position-absolute d-none"
                      role="status"
                    >
                      <span class="visually-hidden"></span>
                    </div>
                  </div>
                </td>

                <td>
                  <button
                    className={
                      l.traspaso === "0" ? "d-none" : "border-0 bg-transparent"
                    }
                    onClick={(e) => imprimirLiquidaciones(e, l)}
                  >
                    <img width={25} src={printer} alt=""></img>
                  </button>
                </td>

                <td>
                  <button
                    className={
                      l.traspaso === "0"
                        ? "d-none mx-4"
                        : "border-0 bg-transparent mx-4"
                    }
                    onClick={(e) => {
                      enviarLiquidaciones(e, l);
                    }}
                  >
                    <img width={25} src={mail} alt=""></img>
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </Table>
      <div>
        {liquidaciones.length === 0 ? (
          <div className="footer text-center">No se registra información</div>
        ) : (
          ""
        )}
      </div>
    </div>
  );
}
