import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../../style/styles.css";
import arrowLeft from "../../utils/left-arrow.png";
import Swal from "sweetalert2";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux/es/exports";

export default function Docente() {
  const { id, descripcion } = useParams();
  const navigation = useNavigate();

  const dispatch = useDispatch();

  const aranceles = useSelector((state) => state.aranceles);
  const [comboCiclos, setComboCiclos] = useState([]);

  let [input, setInput] = useState({
    idbeca: "",
    nombre: "",
    tipo: "",
    valor: "",
  });

  useEffect(() => {
    CargarComboCiclos();
    if (id !== "0") {
      axios
        .get(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/traerdatosbeca.php?idbeca=" +
            id
        )
        .then((response) => {
          setInput({
            idbeca: response.data[0][0],
            nombre: response.data[0][1],
            tipo: response.data[0][2],
            valor: response.data[0][3],
          });
        });
    }
  }, [dispatch]);

  async function CargarComboCiclos() {
    await axios
      .get(
        "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/liquidacionindividual/combociclos.php"
      )
      .then((response) => {
        setComboCiclos(response?.data);
      });
  }

  function handleChange(e) {
    e.preventDefault();
    setInput((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  }

  function editarArancel(e) {
    e.preventDefault();
    if (id === "0") {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/grabardatosbecas.php?oper=" +
            "A" +
            "&idbeca=" +
            0 +
            "&nombre=" +
            input.nombre +
            "&tipo=" +
            input.tipo +
            "&valor=" +
            input.valor
        )
        .then((response) => {
          console.log(response.data);
          if (response.data === "No se puede completar la operaci칩n") {
            Swal.fire({
              title: "Error al crear Beca!",
              icon: "error",
              html: `${response.data}`,
              showCloseButton: true,
            }).then((result) => {
              if (result.isConfirmed) {
                navigation("/becas");
              }
            });
          }
          navigation("/becas");
        });
    } else {
      axios
        .post(
          "https://www.califcolegios.wnpower.host/donboscocastelarweb/app/grabardatosbecas.php?oper=" +
            "M" +
            "&idbeca=" +
            id +
            "&nombre=" +
            input.nombre +
            "&tipo=" +
            input.tipo +
            "&valor=" +
            input.valor
        )
        .then((response) => {
          console.log(response.data);
        });
      navigation("/becas");
    }
    document.querySelector(".navbar").classList.remove("active-nav");
    document.querySelector(".contenedor").classList.remove("active-contenedor");
  }

  return (
    <React.Fragment>
      <div className="pr-5 pt-3 pl-3 pl-lg-5 ml-lg-5 contenedor">
        <div className="pt-5">
          <div>
            <h4 className="">BECAS</h4>
            <p className="text-secondary">
              Crear y gestionar los datos de los Aranceles
            </p>
          </div>
          <hr className="w-100 mx-0" />
          <div className="d-flex justify-content-between w-100">
            <h4>
              Informacion del Arancel {"  "}
              <strong className="ml-3 text-secondary font-weight-normal">
                {input.nombre}
              </strong>
            </h4>
            <div>
              <button
                type="button"
                className="btn"
                id="id_cancelar"
                onClick={() => {
                  navigation("/becas");
                  document
                    .querySelector(".navbar")
                    .classList.remove("active-nav");
                  document
                    .querySelector(".contenedor")
                    .classList.remove("active-contenedor");
                }}
              >
                {" "}
                <img width="20px" src={arrowLeft} alt="arrow-left" />
                Listado
              </button>
            </div>
          </div>
          <hr className="w-100 mx-0" />
          <p className="text-secondary">Gestionar la informacion del Arancel</p>
        </div>
        <nav>
          <ul class="nav nav-tabs" id="myTab" role="tablist">
            <li class="nav-item" role="presentation">
              <button
                class="nav-link active"
                id="datos-tab"
                data-bs-toggle="tab"
                data-bs-target="#datos"
                type="button"
                role="tab"
                aria-controls="datos"
                aria-selected="true"
                style={{ outline: 0 }}
              >
                Datos B치sicos
              </button>
            </li>
            <li class="nav-item" role="presentation">
              <button
                class="nav-link"
                id="conceptos-tab"
                data-bs-toggle="tab"
                data-bs-target="#conceptos"
                type="button"
                role="tab"
                aria-controls="conceptos"
                aria-selected="false"
                style={{ outline: 0 }}
              >
                Conceptos
              </button>
            </li>
            <li class="nav-item" role="presentation">
              <button
                class="nav-link"
                id="cursosAlumnos-tab"
                data-bs-toggle="tab"
                data-bs-target="#cursosAlumnos"
                type="button"
                role="tab"
                aria-controls="cursosAlumnos"
                aria-selected="false"
                style={{ outline: 0 }}
              >
                Cursos y Alumnos
              </button>
            </li>
          </ul>
        </nav>

        <div class="tab-content" id="myTabContent">
          <div
            class="tab-pane fade show active"
            id="datos"
            role="tabpanel"
            aria-labelledby="datos-tab"
          >
            {/* DATOS BASICOS */}
            <div className="w-100 mt-3">
              <form
                name="f_abm_docentes"
                id="id_abm_docentes"
                className=""
                onSubmit={(e) => editarArancel(e)}
              >
                <div className="form-group row align-items-center">
                  <label
                    htmlFor="nombre"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Descripci칩n
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="nombre"
                      name={"nombre"}
                      value={input.nombre}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        aranceles.descripcion
                          ? aranceles.descripcion
                          : "Ingrese Nombre"
                      }
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="tipo"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Tipo
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <select
                      required
                      className="form-control"
                      id="tipo"
                      name={"tipo"}
                      value={input.tipo}
                      onChange={(e) => handleChange(e)}
                    >
                      <option value="0">Seleccionar tipo</option>
                      <option value="1">CUOTA</option>
                      <option value="5">CUOTA2</option>
                      <option value="2">MATRICULA</option>
                      <option value="3">DEUDA</option>
                    </select>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="valor"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Fecha 1er Vto.
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="valor"
                      name={"valor"}
                      value={input.valor}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        aranceles.fecha1 ? aranceles.fecha1 : "Ingrese Fecha"
                      }
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="valor"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Importe 1er Vto.
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="valor"
                      name={"valor"}
                      value={input.valor}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        aranceles.fecha1 ? aranceles.fecha1 : "Ingrese Fecha"
                      }
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="valor"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Fecha 2do Vto.
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="valor"
                      name={"valor"}
                      value={input.valor}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        aranceles.fecha2 ? aranceles.fecha2 : "Ingrese Fecha"
                      }
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="valor"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Importe 2do Vto.
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="valor"
                      name={"valor"}
                      value={input.valor}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        aranceles.fecha1 ? aranceles.fecha1 : "Ingrese Fecha"
                      }
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="tipo"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Mes
                  </label>
                  <div className="col-sm-4 col-lg-3">
                    <select
                      required
                      className="form-control"
                      id="tipo"
                      name={"tipo"}
                      value={input.tipo}
                      onChange={(e) => handleChange(e)}
                    >
                      <option value="0">Seleccionar Mes</option>
                      <option value="1">ENERO</option>
                      <option value="2">FEBRERO</option>
                      <option value="3">MARZO</option>
                      <option value="4">ABRIL</option>
                      <option value="5">MAYO</option>
                      <option value="6">JUNIO</option>
                      <option value="7">JULIO</option>
                      <option value="8">AGOSTO</option>
                      <option value="9">SEPTIEMBRE</option>
                      <option value="10">OCTUBRE</option>
                      <option value="11">NOVIEMBRE</option>
                      <option value="12">DICIEMBRE</option>
                    </select>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="valor"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Recargo Diario
                  </label>
                  <div class="input-group col-sm-4 col-lg-3">
                    <input
                      aria-label="Recipient's username"
                      aria-describedby="basic-addon2"
                      required
                      type="text"
                      className="form-control"
                      id="valor"
                      name={"valor"}
                      value={input.valor}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        aranceles.fecha2 ? aranceles.fecha2 : "Ingrese Fecha"
                      }
                    />
                    <span class="input-group-text" id="basic-addon2">
                      %
                    </span>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="tipo"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Ciclo
                  </label>
                  <select className="form-control col-sm-4 col-lg-3">
                    {comboCiclos &&
                      comboCiclos?.map((ciclo) => {
                        if (ciclo[0] === `${new Date().getFullYear()}`) {
                          return (
                            <option selected key={ciclo[0]}>
                              {ciclo[0]}
                            </option>
                          );
                        }
                        return <option key={ciclo[0]}>{ciclo[0]}</option>;
                      })}
                  </select>
                </div>

                <div className="w-100 fixed-bottom d-flex justify-content-end border-top bg-light pr-5">
                  <button
                    type="button"
                    className="btn btn-secondary px-3 m-3 text-light shadow-sm"
                    id="id_cancelar"
                    onClick={() => {
                      navigation("/aranceles");
                      document
                        .querySelector(".navbar")
                        .classList.remove("active-nav");
                      document
                        .querySelector(".contenedor")
                        .classList.remove("active-contenedor");
                    }}
                  >
                    Descartar Cambios
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary px-3 m-3 text-light shadow-sm"
                    id="id_grabar"
                  >
                    Guardar
                  </button>
                </div>
              </form>
            </div>
          </div>
          <div
            class="tab-pane fade"
            id="conceptos"
            role="tabpanel"
            aria-labelledby="conceptos-tab"
          ></div>
          <div
            class="tab-pane fade"
            id="cursosAlumnos"
            role="tabpanel"
            aria-labelledby="cursosAlumnos-tab"
          ></div>
        </div>
      </div>
    </React.Fragment>
  );
}
