            </div>
          </div>
          <div
            class="tab-pane fade"
            id="materias"
            role="tabpanel"
            aria-labelledby="materias-tab"
          >
            <DocentesVinc id={id} />
          </div>
        </div>
      </div>
    </React.Fragment>
  );
