                      type="text"
                      className="form-control"
                      id="apellido"
                      name={"apellido"}
                      value={input.apellido}
                      onChange={(e) => handleChange(e)}
                      placeholder={user[1] ? user[1] : "Ingrese Apellido"}
                    ></input>
                  </div>
                </div>

