                    <input
                      required
                      type="text"
                      className="form-control"
                      id="nombres"
                      name={"nombres"}
                      value={input.nombres}
                      onChange={(e) => handleChange(e)}
                      placeholder={user[2] ? user[2] : "Ingrese Nombres"}
                    ></input>
                  </div>
                </div>

