                    <input
                      required
                      type="number"
                      className="form-control"
                      id="inputBox"
                      name={"docnro"}
                      value={input.docnro}
                      onChange={(e) => handleChange(e)}
                      placeholder={user[3] ? user[3] : "Ingrese Documento"}
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="email"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Tipo
                  </label>
                  <div className="col-sm-6">
                    <input
                      required
                      type="email"
                      className="form-control"
                      id="email"
                      name={"email"}
                      value={input.email}
                      onChange={(e) => handleChange(e)}
                      placeholder={
                        user[4] ? user[4] : "Ingrese Correo Electrónico"
                      }
                    ></input>
                  </div>
                </div>

                <div className="form-group row align-items-center">
                  <label
                    htmlFor="domicilio"
                    className="col-sm-2 control-label text-label h6"
                  >
                    Agrupación
                  </label>
                  <div className="col-sm-6">
                    <input
                      required
                      type="text"
                      className="form-control"
                      id="domicilio"
                      name={"domicilio"}
                      value={input.domicilio}
                      onChange={(e) => handleChange(e)}
                      placeholder={user[5] ? user[5] : "Ingrese el Domicilio"}
                    ></input>
                  </div>
                </div>

                <input
                  type="hidden"
                  id="iddocente"
                  name={"iddocente"}
                  value={input.iddocente}
                  style={{ display: "none" }}
                ></input>
                <input
                  type="hidden"
                  name={"doper"}
                  value={"A"}
                  style={{ display: "none" }}
                ></input>

                <div className="w-100 fixed-bottom d-flex justify-content-end border-top bg-light pr-5">
                  <button
                    type="button"
                    className="btn btn-secondary px-3 m-3 text-light shadow-sm"
                    id="id_cancelar"
                    onClick={() => {
                      navigation("/home");
                      document
                        .querySelector(".navbar")
                        .classList.remove("active-nav");
                      document
                        .querySelector(".contenedor")
                        .classList.remove("active-contenedor");
                    }}
                  >
                    Descartar Cambios
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary px-3 m-3 text-light shadow-sm"
                    id="id_grabar"
                  >
                    Guardar
                  </button>
                </div>
              </form>
            </div>
          </div>
          <div
            class="tab-pane fade"
            id="materias"
            role="tabpanel"
            aria-labelledby="materias-tab"
          >
            <DocentesVinc id={id} />
          </div>
        </div>
      </div>
    </React.Fragment>
  );
